//       setPage(prevPage => prevPage + 1);

//     } catch (error) {
//       console.log('Smth wrong with App fetch', error);
//       setError({ error });
//     } finally {
//       setLoading(false);
//     }
//   };

    render() { 
        const { image, error, status } = this.state;
        const { imageName } = this.props

        if (status === 'idle') {
            return 
        }

        if (status === 'pending') {
            return <Loader/>
        }

        if (status === 'rejected') { 
            return alert(error)
        }

        if (status === 'resolved') {
            return (
                <div><ImageGallery images={image} />
                {(
            <Button onClick={this.onNextPage} />
          )}</div>)
        }
    }
}
